CREATE SCHEMA IF NOT EXISTS `jardineria` DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_bin;
use jardineria;

DROP TABLE IF EXISTS DETALLEPEDIDOS, PEDIDOS, PAGOS, PRODUCTOS, CLIENTES, GAMASPRODUCTOS, EMPLEADOS, OFICINAS;

CREATE TABLE OFICINAS (
    CODIGOOFICINA VARCHAR(10) PRIMARY KEY,
    CIUDAD VARCHAR(30) NOT NULL,
    PAIS VARCHAR(50) NOT NULL,
    REGION VARCHAR(50),
    CODIGOPOSTAL VARCHAR(10) NOT NULL,
    TELEFONO VARCHAR(20) NOT NULL,
    LINEADIRECCION1 VARCHAR(50) NOT NULL,
    LINEADIRECCION2 VARCHAR(50)
);

CREATE TABLE EMPLEADOS (
    CODIGOEMPLEADO INT AUTO_INCREMENT PRIMARY KEY NOT NULL,
    NOMBRE VARCHAR(50) NOT NULL,
    APELLIDO1 VARCHAR(50) NOT NULL,
    APELLIDO2 VARCHAR(50),
    EXTENSION VARCHAR(10) NOT NULL,
    EMAIL VARCHAR(100) NOT NULL,
    CODIGOOFICINA VARCHAR(10) NOT NULL,
    CODIGOJEFE INT,
    PUESTO VARCHAR(50),
    CONSTRAINT FK_EMPLEADOS_OFICINAS FOREIGN KEY (CODIGOOFICINA) REFERENCES OFICINAS(CODIGOOFICINA),
    CONSTRAINT FK_EMPLEADOS_EMPLEADOS FOREIGN KEY (CODIGOJEFE) REFERENCES EMPLEADOS(CODIGOEMPLEADO)
);

CREATE TABLE CLIENTES (
    CODIGOCLIENTE INT AUTO_INCREMENT PRIMARY KEY NOT NULL,
    NOMBRECLIENTE VARCHAR(50) NOT NULL,
    NOMBRECONTACTO VARCHAR(30),
    APELLIDOCONTACTO VARCHAR(30),
    TELEFONO VARCHAR(15) NOT NULL,
    FAX VARCHAR(15) NOT NULL,
    LINEADIRECCION1 VARCHAR(50) NOT NULL,
    LINEADIRECCION2 VARCHAR(50),
    CIUDAD VARCHAR(50) NOT NULL,
    REGION VARCHAR(50),
    PAIS VARCHAR(50) NOT NULL,
    CODIGOPOSTAL VARCHAR(10),
    CODIGOEMPLEADOREPVENTAS INT,
    LIMITECREDITO DECIMAL(15,2),
    CONSTRAINT FK_CLIENTES_EMPLEADOS FOREIGN KEY (CODIGOEMPLEADOREPVENTAS) REFERENCES EMPLEADOS(CODIGOEMPLEADO)
);

CREATE TABLE PEDIDOS (
    CODIGOPEDIDO INT AUTO_INCREMENT PRIMARY KEY,
    FECHAPEDIDO DATE NOT NULL,
    FECHAESPERADA DATE NOT NULL,
    FECHAENTREGA DATE,
    ESTADO VARCHAR(15) NOT NULL,
    COMENTARIOS TEXT,
    CODIGOCLIENTE INT NOT NULL,
    CONSTRAINT FK_PEDIDOS_CLIENTES FOREIGN KEY (CODIGOCLIENTE) REFERENCES CLIENTES(CODIGOCLIENTE)
);

CREATE TABLE GAMASPRODUCTOS (
    GAMA VARCHAR(50) PRIMARY KEY,
    DESCRIPCIONTEXTO TEXT,
    DESCRIPCIONHTML TEXT,
    IMAGEN LONGBLOB
);

CREATE TABLE PRODUCTOS (
    CODIGOPRODUCTO VARCHAR(15) PRIMARY KEY,
    NOMBRE VARCHAR(70) NOT NULL,
    GAMA VARCHAR(50) NOT NULL,
    DIMENSIONES VARCHAR(25),
    PROVEEDOR VARCHAR(50),
    DESCRIPCION TEXT,
    CANTIDADENSTOCK INT NOT NULL,
    PRECIOVENTA DECIMAL(15,2) NOT NULL,
    PRECIOPROVEEDOR DECIMAL(15,2),
    CONSTRAINT FK_PRODUCTOS_GAMAS FOREIGN KEY (GAMA) REFERENCES GAMASPRODUCTOS(GAMA)
);

CREATE TABLE DETALLEPEDIDOS (
    CODIGOPEDIDO INT NOT NULL,
    CODIGOPRODUCTO VARCHAR(15) NOT NULL,
    CANTIDAD INT NOT NULL,
    PRECIOUNIDAD DECIMAL(15,2) NOT NULL,
    NUMEROLINEA INT NOT NULL,
    PRIMARY KEY (CODIGOPEDIDO, CODIGOPRODUCTO),
    CONSTRAINT FK_DETALLEPEDIDOS_PEDIDOS FOREIGN KEY (CODIGOPEDIDO) REFERENCES PEDIDOS(CODIGOPEDIDO),
    CONSTRAINT FK_DETALLEPEDIDOS_PRODUCTOS FOREIGN KEY (CODIGOPRODUCTO) REFERENCES PRODUCTOS(CODIGOPRODUCTO)
);

CREATE TABLE PAGOS (
    CODIGOCLIENTE INT NOT NULL,
    FORMAPAGO VARCHAR(40) NOT NULL,
    IDTRANSACCION VARCHAR(50) NOT NULL,
    FECHAPAGO DATE NOT NULL,
    CANTIDAD DECIMAL(15,2) NOT NULL,
    PRIMARY KEY (CODIGOCLIENTE, IDTRANSACCION),
    CONSTRAINT FK_PAGOS_CLIENTES FOREIGN KEY (CODIGOCLIENTE) REFERENCES CLIENTES(CODIGOCLIENTE)
);

